import React, {useState , useEffect} from 'react'

function Calculator() {
  const [data, setData] = useState([{
    firstNum: 0,
    secondNum: 0,
    operator: '+',
  }]);
  //const [firstNum, setFirstNum] = useState(0);
  //const [secondNum, setSecondNum] = useState(0);
  //const [operator, setOperator] = useState('+');
  const [result, setResult] = useState(0);
  let calcFunc = () => {
    if(data.operator === '+'){
      const rslt = parseFloat(data.firstNum) + parseFloat(data.secondNum);
      setResult(rslt);
    }
    else if(data.operator === '-'){
      const rslt = parseFloat(data.firstNum) - parseFloat(data.secondNum);
      setResult(rslt);
    }
    else if(data.operator === '*'){
      const rslt = parseFloat(data.firstNum) * parseFloat(data.secondNum);
      setResult(rslt);
    }
    else if(data.operator === '/'){
      const rslt = parseFloat(data.firstNum) / parseFloat(data.secondNum);
      setResult(rslt);
    }
  }

  useEffect(() => {
    calcFunc();
    console.log(data.operator);
  })

  return (
    <div>
        <input placeholder="First number" onChange={(e) => (setData([...data ,{firstNum: e.target.value}]))}/>
        <select onChange={(e) => (setData([...data ,{operator: e.target.value}]))}>
          <option value="+">+</option>
          <option value="-" >-</option>
          <option value="*" >*</option>
          <option value="/" >/</option>
        </select>
        <input placeholder="Second number" onChange={(e) => (setData([...data ,{secondNum: e.target.value}]))}/>
        <div>
          {result}
        </div>
    </div> 
  )
}

export default Calculator;
